<div class="row">
    <div class="col-sm-8">
        <h2>Переписка</h2>
        <? if (empty($question) === false):?>
            <div class="comments">
                <? foreach ($question->messages as $item):?>
                    <? if ($item->type == QuestionMessage::TYPE_USER):?>
                        <div class="media" style="color: #229ccb;">
                            <a class="pull-left" href="javascript:void(0)">
                                <img class="media-object" style="width: 64px; height: 64px;" src="data:image/png;base64,">
                            </a>
                            <div class="media-body">
                                <h4 class="media-heading"><?= $question->user->username?></h4>
                                <p><?= $item->message?><br><a class="small"><?= $item->date?></a></p>
                            </div>
                        </div>
                    <? else:?>
                        <div class="media" style="margin-left: 75px; color: #6fa236;;">
                            <a class="pull-left" href="javascript:void(0)">
                                <img class="media-object" style="width: 64px; height: 64px;" src="data:image/png;base64,">
                            </a>
                            <div class="media-body">
                                <h4 class="media-heading">Администратор</h4>
                                <p><?= $item->message?><br><a class="small"><?= $item->date?></a></p>
                            </div>
                        </div>
                    <? endif;?>
                <? endforeach;?>
            </div>
        <? endif;?>
        <form class="form" role="form" method='POST' action=''>
            <div class="form-group">
                <textarea name="message" class="form-control col-md-12" rows="8" name="message"></textarea>
            </div>
            <input type="submit" value="Ответить" class="btn btn-primary" style="display: block;">
        </form>
    </div>
</div>
